package Testing;

public class Multiplication {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int n=2; 
     for(int i=1;i<=10;i++) {
     System.out.println(n+ "*" + i + "=" + (n*i));
     }
     
     int y=5;
     for(int j=1;j<=10;j++) {
     System.out.println(y+ "*" + j + "=" + (y*j));	
     }
     
     n=3;
     for(int k=1;k<=10;k++) {
    	 System.out.println(n+ "*" + k + "=" +(n*k));
    
     }
	}
}
 